import { useState } from "react";
import "./App.css";
import ChatBox from "./ChatBox";
import Input from "./Input";

function App() {
  const [messages, setMessages] = useState([]);
  const [currentValue, setCurrentValue] = useState("");
  const [currentValue2, setCurrentValue2] = useState("");
  const onSend = () => {
    setMessages([...messages, currentValue]);
    setCurrentValue("");
  };
  const onSend2 = () => {
    setMessages([...messages, currentValue2]);
    setCurrentValue2("");
  };
  const getValue = (e) => {
    return setCurrentValue(e.target.value);
  };
  const getValue2 = (e) => {
    setCurrentValue2(e.target.value);
  };
  return (
    <div className="App">
      <ChatBox messageArr={messages} />
      <Input
        user="user1"
        inputChange={getValue}
        send={onSend}
        current={currentValue}
      />
      <Input
        user="user2"
        inputChange={getValue2}
        send={onSend2}
        current={currentValue2}
      />
    </div>
  );
}

export default App;
