import React from "react";
import Message from "./Message";
function ChatBox({ messageArr, userId }) {
  return (
    <div>
      {messageArr.map((msg) => {
        return <Message text={msg} key={msg} user={userId} />;
      })}
    </div>
  );
}

export default ChatBox;
